import Foundation

func getFirstCharacter(_ s:String) -> Int {
    
    var character = [Character]()
    
    for char in s {
        character.append(char)
    }
    
    var dic = [Character:Int]()
    
    for i in 0..<s.count {
        let cur = dic[character[i]] ?? 0
        dic[character[i]]  = cur + 1
    }
    
    for i in 0..<s.count {
        if dic[character[i]] == 1 {
            return i
        }
    }
    
    return -1
}



