func firstUniqChar(_ s: String) -> Int {
    
    var arr = [Int]()

    for _ in 0..<26 {
        arr.append(0)
    }

    for scalar in s.unicodeScalars {
        let index = Int(scalar.value - 97)
        let cur = arr[index]
        arr[index] = cur + 1
    }

    var first = 0
    
    for scalar in s.unicodeScalars {
        let index = Int(scalar.value - 97)
        if arr[index] == 1 {
            return first
        }
        first += 1
    }
    
    return -1
}




